SELECT menu.id AS id, menu.parent_id AS parentId, menu.unshared AS unshared, menu.type AS type, menu.`name` AS `name`, menu.`abbreviation_name` AS `abbreviationName`, menu.icon AS icon, menu.perms AS perms, menu.path AS path, menu.`order` AS `order`, menu.version AS version FROM `t_menu` AS menu WHERE menu.parent_id = #{parentId} AND menu.client_type = #{clientType.value} AND menu.deleted = 0
AND menu.unshared = #{unshared}
ORDER BY menu.order

SELECT menu.id AS id, menu.parent_id AS parentId, menu.type AS type, menu.`name` AS `name`, menu.icon AS icon, menu.perms AS perms, menu.path AS path, menu.`order` AS `order`, menu.version AS version FROM `t_menu` AS menu WHERE ( menu.type = 0 OR menu.id in (
#{menuId}
) ) AND menu.parent_id = #{parentId} AND menu.client_type = #{clientType.value} AND menu.unshared = 0 AND menu.deleted = 0 ORDER BY menu.order
